#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  $exe=".exe"
}
$ret=0
if (Test-Path "$basedir/node$exe") {
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../.deno/raylib@0.14.0/node_modules/raylib/bin/node-raylib" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../.deno/raylib@0.14.0/node_modules/raylib/bin/node-raylib" $args
  }
  $ret=$LASTEXITCODE
} else {
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../.deno/raylib@0.14.0/node_modules/raylib/bin/node-raylib" $args
  } else {
    & "node$exe"  "$basedir/../.deno/raylib@0.14.0/node_modules/raylib/bin/node-raylib" $args
  }
  $ret=$LASTEXITCODE
}
exit $ret
